insert into t_quest values (null, '★6', 213, '任务', '惊愕的！毒妖鸟！调查！', 'HR11', '接待员:大蚁冢荒地中有毒妖鸟！？光那样就已经很奇妙了，而且这个毒妖鸟还比一般的更强韧。伙伴，赶紧去调查这个现象吧！', '大蚁冢荒地', '毒妖鸟', '上铠玉:2:1% (保底)|上龙骨:1:30%|龙骨【大】:2:18%|良质硬骨:1:16%|灵鹤石:1:10%|雷光虫:3:10%|谜之宝珠:1:15%', '·限制时间结束|·3次力尽倒下', '狩猎毒妖鸟', 50, 7200, '', 'Invader in the Waste', '驚愕の！　プケプケ！　調査！');
insert into t_quest values (null, '★6', 213, '任务', '暴徒变得更粗暴了', 'HR11', '生态研究所所长:发现啦，强韧的蛮颚龙！　蛮颚龙的许多地方都能作为研究新大陆的标准，我可不能放过这个贵重的机会。就麻烦你狩猎蛮颚龙咯。', '大蚁冢荒地', '蛮颚龙', '上铠玉:2:1% (保底)|尖龙骨:1:30%|上龙骨:2:18%|良质硬骨:1:16%|灵鹤石:1:10%|雷光虫:3:10%|谜之宝珠:1:15%', '·限制时间结束|·3次力尽倒下', '狩猎蛮颚龙', 50, 9000, '', 'Tickled Pink', '暴れん坊、さらに暴れる');
insert into t_quest values (null, '★7', 218, '任务', '神秘王国的女王', 'HR12', '总司令:在各地留下奇妙的痕迹的怪物，其痕迹与陆之女王雌火龙似是而非，我们已经查明，它现在正位于陆珊瑚台地。请调查班承接任务，并尽快前往当地进行调查。', '陆珊瑚台地', '', '尖铠玉:1:1% (保底)|坚龙骨:1:24%|尖龙骨:1:20%|灵鹤石:1:20%|谜之宝珠:1:18%|精晶原石:1:12%|璀璨水晶:1:5%', '·限制时间结束|·3次力尽倒下', '达成任务', 50, 12600, '', 'Old World Monster in the New World', '不思議の国の女王');
insert into t_quest values (null, '★8', 214, '任务', '渴望的黑创', 'HR13', '总司令:借助古龙迁徙的机会，我们终于查出了与我们长年隔岸观望的神秘古龙灭尽龙的所在之处了。调查它之后，我们将有望更加接近古龙迁徙的真相。', '龙结晶之地', '灭尽龙', '勇气之证·大:1:1% (保底)|古龙骨:1:24%|古龙之血:1:18%|幻晶原石:1:8%|龙脉结晶:2:15%|谜之宝珠:1:18%|尖铠玉:2:16%', '·限制时间结束|·3次力尽倒下', '讨伐灭尽龙', 50, 18000, '', 'A Wound and a Thirst', '渇望の黒創');
insert into t_quest values (null, '★8', 214, '任务', '钢铁的钢龙', 'HR14', '调查班队长:在森林留下痕迹的古龙是钢龙钢龙。现在我们已经知道它们在龙结晶之地了。为了守护星辰，也为了调查古龙，调查班的各位，拜托你们了！', '龙结晶之地', '钢龙', '勇气之证·大:1:1% (保底)|古龙骨:1:24%|古龙之血:1:18%|幻晶原石:1:8%|龙脉结晶:2:15%|谜之宝珠:1:18%|尖铠玉:2:16%', '·限制时间结束|·3次力尽倒下', '讨伐钢龙', 50, 18000, '', 'Kushala Daora, Dragon of Steel', '鋼鉄のクシャルダオラ');
insert into t_quest values (null, '★8', 214, '任务', '爆炎的炎王龙', 'HR14', '剑术大师:在荒地中昂首阔步的就是那个炎帝炎王龙。那家伙现在正坐镇于龙结晶之地。年轻的猎人们啊，现在就出发去拜会那家伙吧！', '龙结晶之地', '炎王龙', '勇气之证·大:1:1% (保底)|古龙骨:1:24%|古龙之血:1:18%|幻晶原石:1:8%|龙脉结晶:2:15%|谜之宝珠:1:18%|尖铠玉:2:16%', '·限制时间结束|·3次力尽倒下', '讨伐炎王龙', 50, 18000, '', 'Teostra the Infernal', '爆炎のテオ・テスカトル');
insert into t_quest values (null, '★8', 214, '任务', '冥底的尸套龙', 'HR14', '第三期团的期团长:过去有着人们只知其存在，但未见过其身影的古龙。那就是尸套龙。它大概就是让瘴气变浓的原因。这次绝对要找出将那家伙，并彻底调查喔。', '瘴气之谷', '尸套龙', '勇气之证·大:1:1% (保底)|古龙骨:1:24%|古龙之血:1:18%|幻晶原石:1:8%|龙脉结晶:2:15%|谜之宝珠:1:18%|尖铠玉:2:16%', '·限制时间结束|·3次力尽倒下', '讨伐尸套龙', 50, 18000, '', 'Hellish Fiend Vaal Hazak', '冥底のヴァルハザク');
insert into t_quest values (null, '★9', 218, '任务', '收束之地', 'HR15', '大团长:现在新大陆的地脉中正流动着异常大量的能量。我们已经查出能量集中的地点了。现在要调查存在于那里的东西。做好乘船并跟随星之指引的准备吧。', '地脉收束地', '', '勇气之证·大:1:1% (保底)|古龙骨:1:24%|古龙之血:1:18%|幻晶原石:1:13%|龙脉结晶:2:16%|古老龙骨:1:12%|坚铠玉:1:16%', '·限制时间结束|·3次力尽倒下', '达成任务', 50, 19800, '', 'Land of Convergence', '収束の地');
insert into t_quest values (null, '★9', 213, '任务', '超越爆鳞之道', 'HR29', '总司令:只有对于自身技术有坚定自信者，才允许接受委托。对象是2头爆鳞龙。但这是比通常种还要强大的个体，唯有越过这道难关者，才能被认可为真正的猎人。', '大蚁冢荒地', '爆鳞龙|爆鳞龙', '龙脉石:1:7% (保底)|坚龙骨:2:20%|爆鳞龙的尖爪:1:18%|爆鳞龙之翼:1:18%|爆鳞龙的爆腺:1:14%|爆鳞龙的宝玉:1:5%|坚铠玉:1:18%', '·限制时间结束|·3次力尽倒下', '狩猎2头爆鳞龙', 50, 27720, 'HR29以上', 'Beyond the Blasting Scales', '爆ぜる鱗を超えた道');
insert into t_quest values (null, '★9', 214, '任务', '此雷鸣是天罚？亦或是祝福？', 'HR49', '总司令:只有胸怀大志者，才允许接受委托。前往陆珊瑚台地，调查身缠轰雷的幻兽吧。在此困难的前方，一定存在着身为猎人的壮志。', '陆珊瑚台地', '麒麟', '大龙脉石:1:7% (保底)|古龙骨:1:21%|古龙之血:1:18%|龙脉石:1:13%|龙晶原石:1:11%|坚铠玉:1:18%|璀璨水晶:1:12%', '·限制时间结束|·3次力尽倒下', '讨伐麒麟', 50, 21600, 'HR49以上', 'Thunderous Rumble in the Highlands', 'その雷鳴は天罰か、祝福か');
insert into t_quest values (null, 'M★1', 213, '任务', '初次洗礼！？', 'MR1', '接待员:欢迎来到新世界！这完全被冰雪覆盖的世界，令人吃惊。但还是得先找找能让第三期团的热气球降落的地点。我来找适合的地点，伙伴请负责确保安全！', '永霜冻土', '冰鱼龙', '太古的宝珠:1:1% (保底)|强龙骨:1:43%|坚铠玉:1:20%|雪光金属:1:10%|冰豺狼的厚毛皮:1:13%|落阳草的花:1:13%', '·限制时间结束|·3次力尽倒下', '狩猎冰鱼龙', 50, 14400, '完成本体主线后发现 冰鱼龙', 'Baptism by Ice', 'はじまりの洗礼！？');
insert into t_quest values (null, 'M★1', 213, '任务', '粗活就交给猛牛龙', 'MR2', '总司令:安排了从星辰运送建筑资材。预定近期运送队就将渡海而来。有劳调查班负责确保永霜冻土的安全。千万别忘记防寒的准备。', '永霜冻土', '猛牛龙', '太古的宝珠:1:1% (保底)|强龙骨:1:43%|坚铠玉:1:20%|雪光金属:1:10%|冰豺狼的厚毛皮:1:13%|落阳草的花:1:13%', '·限制时间结束|·3次力尽倒下', '狩猎猛牛龙', 50, 14400, '完成"初次洗礼！？"', 'Banbaro Blockade', '力仕事はバフバロにお任せ');
insert into t_quest values (null, 'M★2', 213, '任务', '冰土守门者·痹毒龙', 'MR3', '原野大师:看来若不赶走痹毒龙，很难前往导虫指示的地点。那里说不定会有什么重要的东西。请一定要小心！', '永霜冻土', '痹毒龙', '太古的宝珠:1:1% (保底)|强龙骨:1:43%|坚铠玉:1:20%|雪光金属:1:10%|冰豺狼的厚毛皮:1:13%|落阳草的花:1:13%', '·限制时间结束|·3次力尽倒下', '狩猎痹毒龙', 50, 18000, '完成"粗活就交给猛牛龙"和发现发现 痹毒龙', 'Ready to Strike', '氷土の番人・トビカガチ亜種');
insert into t_quest values (null, 'M★2', 213, '任务', '眠之飞龙与四颗眼珠', 'MR4', '好胜的推荐组员:浮眠龙。第一位发现者因感受到了强烈睡意侵袭，便退下返回了。所以暂时要拜托你了，好伙伴。打起精神完美地调查吧。', '大蚁冢荒地', '浮眠龙', '太古的宝珠:1:1% (保底)|强龙骨:1:43%|坚铠玉:1:20%|顽强重骨:1:10%|鸣袋:2:13%|怪力种子:2:13%', '·限制时间结束|·3次力尽倒下', '狩猎浮眠龙', 50, 18000, '完成"冰土守门者·痹毒龙"', 'No Time for Naps', 'ねむりの飛竜とよっつの目玉');
insert into t_quest values (null, 'M★2', 213, '任务', '占领饮水处吧！', 'MR4', '好胜的推荐组员:水妖鸟喝下了大量的水之后，吐出水团攻击了第一位发现者。所以暂时要拜托你了，好伙伴。打起精神完美地调查吧。', '陆珊瑚台地', '水妖鸟', '太古的宝珠:1:1% (保底)|强龙骨:1:43%|坚铠玉:1:20%|渊水晶:1:10%|强韧之爪:1:13%|打消果实:3:13%', '·限制时间结束|·3次力尽倒下', '狩猎水妖鸟', 50, 18000, '完成"冰土守门者·痹毒龙"', 'Play Both Ends', '水飲ミ場ヲ占拠セヨ！');
insert into t_quest values (null, 'M★3', 213, '任务', '冰冻王国的白骑士', 'MR6', '调查班队长:最近在永霜冻土陆续目击到冰牙龙。我很担心正在调查遇难船只的两位编辑者。我想尽快把她们带回，拜托你确保安全了！', '永霜冻土', '冰牙龙', '太古的宝珠:1:1% (保底)|重龙骨:1:43%|坚铠玉:1:27%|雪光金属:1:13%|冰豺狼的厚毛皮:1:16%', '·限制时间结束|·3次力尽倒下', '狩猎冰牙龙', 50, 25200, '完成"眠之飞龙与四颗眼珠"和"占领饮水处吧！"后去到月辰', 'Blizzard Blitz', '凍てつく国の白い騎士');
insert into t_quest values (null, 'M★3', 213, '任务', '绝影', 'MR7', '总司令:如果冰呪龙来到新大陆的话，生态系统一定会产生混乱。我料想迅龙的出现就是相关征兆，必须尽快进行调查，不可松懈。', '古代树森林', '迅龙', '太古的宝珠:1:1% (保底)|重龙骨:1:43%|坚铠玉:1:27%|绯鸢石:1:13%|顶级的毛皮:1:16%', '·限制时间结束|·3次力尽倒下', '狩猎迅龙', 50, 25200, '完成"冰冻王国的白骑士"', 'Ever-present Shadow', '絶影');
insert into t_quest values (null, 'M★3', 213, '任务', '灼热之刃，斩龙！', 'MR7', '总司令:如果冰呪龙来到新大陆的话，生态系统一定会产生混乱。我料想斩龙的出现就是相关征兆，必须尽快进行调查，不可松懈。', '大蚁冢荒地', '斩龙', '太古的宝珠:1:1% (保底)|重龙骨:1:43%|坚铠玉:1:27%|顽强重骨:1:16%|鸣袋:2:13%', '·限制时间结束|·3次力尽倒下', '狩猎斩龙', 50, 25200, '完成"冰冻王国的白骑士"', 'The Scorching Blade', '灼熱の刃、ディノバルド！');
insert into t_quest values (null, 'M★3', 213, '任务', '绝对强者', 'MR9', '总司令:因冰呪龙的影响而活跃的怪物也现身在瘴气之谷了。快进行轰龙的调查，以掌握冰呪龙行踪的线索。', '瘴气之谷', '轰龙', '太古的宝珠:1:1% (保底)|重龙骨:1:43%|坚铠玉:1:27%|云鸠石:1:13%|怪物特浓体液:1:16%', '·限制时间结束|·3次力尽倒下', '狩猎轰龙', 50, 25200, '完成"绝影"和"灼热之刃，斩龙！"', 'Absolute Power', '絶対強者');
insert into t_quest values (null, 'M★3', 213, '任务', '粉碎的迎击拳', 'MR9', '总司令:因冰呪龙的影响而活跃的怪物也现身在龙结晶之地了。快进行碎龙的调查，以掌握冰呪龙行踪的线索。', '龙结晶之地', '碎龙', '太古的宝珠:1:1% (保底)|重龙骨:1:43%|坚铠玉:1:27%|灵脉结晶:1:13%|翼龙的特上皮:1:16%', '·限制时间结束|·3次力尽倒下', '狩猎碎龙', 50, 25200, '完成"绝影"和"灼热之刃，斩龙！"', 'A Smashing Cross Counter', '粉砕のクロスカウンター');
insert into t_quest values (null, 'M★3', 218, '任务', '来自冰炎一线间', 'MR11', '总司令:在大家的努力下，证明了冰呪龙在龙结晶之地。还不知道它造访龙结晶之地的目的，拜托精锐成员做好进行确认的准备。', '龙结晶之地', '冰呪龙', '太古的宝珠:1:1% (保底)|重龙骨:1:43%|坚铠玉:1:27%|灵脉结晶:1:13%|翼龙的特上皮:1:16%', '·限制时间结束|·3次力尽倒下', '击退冰呪龙', 50, 28800, '完成"粉碎的迎击拳"和"绝对强者"收集足够 ??? 痕迹 (冰呪龙)', 'A Tale of Ice and Fire', '氷と炎のはざまより');
insert into t_quest values (null, 'M★4', 213, '任务', '剎那白雾', 'MR12', '接待员:小心！这与至今遇到的风漂龙有点不同！搜寻行踪不明的阿姨就交给我！请伙伴负责确保周围的安全！', '永霜冻土', '霜翼风漂龙', '太古的宝珠:1:1% (保底)|刚龙骨:1:38%|坚铠玉:2:25%|雪光金属:1:10%|冰豺狼的厚毛皮:1:13%|落阳草的花:1:13%', '·限制时间结束|·3次力尽倒下', '狩猎霜翼风漂龙', 50, 28800, '完成"来自冰炎一线间"后发现 霜翼风漂龙', 'When the Mist Taketh You', '刹那の白霧');
insert into t_quest values (null, 'M★4', 213, '任务', '腐蚀之刃·硫斩龙', 'MR14', '总司令:冰呪龙离开后，确认各地的怪物立即活跃了起来。现在猎人的人手不足，为了成功向月辰运送防卫兵器，想要拜托你进行硫斩龙的调查。打从心底感谢你的协助…！', '瘴气之谷', '硫斩龙', '太古的宝珠:1:1% (保底)|刚龙骨:1:38%|坚铠玉:2:25%|云鸠石:1:10%|怪物特浓体液:1:13%|屠龙果实:2:13%', '·限制时间结束|·3次力尽倒下', '狩猎硫斩龙', 50, 28800, '完成"暴徒大将的狩猎大作战！"去到星辰', 'The Disintegrating Blade', '腐食の刃・ディノバルド亜種');
insert into t_quest values (null, 'M★4', 213, '任务', '凶终隙末·凶爪龙', 'MR14', '总司令:冰呪龙离开后，确认各地的怪物立即活跃了起来。现在猎人的人手不足，为了成功向月辰运送防卫兵器，想要拜托你进行凶爪龙的调查。打从心底感谢你的协助…！', '陆珊瑚台地', '凶爪龙', '太古的宝珠:1:1% (保底)|刚龙骨:1:38%|坚铠玉:2:25%|渊水晶:1:10%|强韧之爪:1:13%|打消果实:3:13%', '·限制时间结束|·3次力尽倒下', '狩猎凶爪龙', 50, 28800, '完成"暴徒大将的狩猎大作战！"去到星辰', 'Bad Friends, Great Enemies', '兇終隙末・オドガロン亜種');
insert into t_quest values (null, 'M★4', 218, '任务', '月辰防卫战', 'MR16', '总司令:冰呪龙的威胁正逼近着月辰。以完成的击龙杭炮攻击，最终目的是击退它。愿指引明路的苍蓝星能为月辰闪烁！', '兵器置放处', '冰呪龙', '坚铠玉:2:21% (保底)|秘药:1:13%|大桶爆弹·大:1:20%|携带食料:4:13%|生命大粉尘:1:33%', '·限制时间结束|·3次力尽倒下|·防卫失败', '达成任务', 50, 28800, '完成"腐蚀之刃·硫斩龙"和"凶终隙末·凶爪龙"', 'The Defense of Seliana', 'セリエナ防衛戦');
insert into t_quest values (null, 'M★4', 213, '任务', '暴徒大将的狩猎大作战！', 'MR13', '调查班队长:近日预定将从星辰运送防卫兵器到永霜冻土。在他们到达前，想先确保路线的安全。拜托你前去狩猎麻烦制造者！', '永霜冻土', '雷颚龙', '太古的宝珠:1:1% (保底)|刚龙骨:1:38%|坚铠玉:2:25%|雪光金属:1:10%|冰豺狼的厚毛皮:1:13%|落阳草的花:1:13%', '·限制时间结束|·3次力尽倒下', '狩猎雷颚龙', 50, 28800, '完成"剎那白雾"', 'The Thunderous Troublemaker!', '暴れん坊大将狩猟大作戦！');
insert into t_quest values (null, 'M★5', 214, '任务', '冰之龙', 'MR17', '调查班队长:虽然守住了月辰，但不知下次何时冰呪龙会发挥真本领。错过这个时机的话，调查团一定难逃毁灭。为了让大家生存下去，将生命托付给追击冰呪龙的精锐们！', '永霜冻土', '冰呪龙', '霸王之证:1:1% (保底)|太古龙骨:1:24%|古龙的净血:1:18%|灵晶原石:1:8%|重铠玉:1:24%|太古的宝珠:1:20%|古老巨龙骨:1:5%', '·限制时间结束|·3次力尽倒下', '讨伐冰呪龙', 50, 28800, '完成"月辰防卫战"', 'The Iceborne Wyvern', '冰の龍');
