insert into t_quest values (null, 'M★6', 214, '自由', '皇妃镇座的炎之宫殿', 'MR50', '物资班队长:收到了炎妃龙的调查委托！坐在火焰宫殿中的她，威风凛凛的真的好帅气。所以要麻烦你好好地调查一番！', '龙结晶之地', '炎妃龙', '霸王之证:1:1% (保底)|太古龙骨:1:24%|古龙的净血:1:18%|灵晶原石:1:8%|重铠玉:1:24%|太古的宝珠:1:20%|古老巨龙骨:1:5%', '·限制时间结束|·3次力尽倒下', '讨伐炎妃龙', 50, 36000, '', 'Into the Palace of Flame', '皇妃座すは炎の宮殿');
insert into t_quest values (null, 'M★6', 217, '自由', '新大陆的猎人们啊', 'MR200', '调查班队长:虽然曾经遭遇许多困难，终于还是抵达了这里。这家伙就交给你了。只要顺利地度过此难关，你就将永远在历史上留名。', '龙结晶之地', '炎王龙|炎妃龙|冰呪龙|歼世灭尽龙', '封印的宝珠:1:1% (保底)|王铠玉:1:21%|刻印的宝珠:1:18%|炎王龙的大鬃毛:1:12%|炎妃龙的刚翼:1:12%|冰龙的刚翼:1:12%|无穷的新生壳:1:12%|古龙的大宝玉:1:7%|冰龙的零玉:1:5%', '·限制时间结束|·3次力尽倒下', '讨伐所有目标', 50, 98640, '', 'Master Hunter of the New World', '新大陸の狩人達よ');
insert into t_quest values (null, 'M★6', 218, '自由', '出来吧，破坏的化身', 'MR24', '大团长:已经知道折断麒麟角的怪物就在聚魔之地了。分析其实力极为强大。千万要小心。就是这样!严禁迟到!', '聚魔之地', '', '太古的宝珠:1:1% (保底)|刚龙骨:1:38%|重龙骨:2:15%|坚铠玉:2:25%|龙晶原石:1:13%|纯水晶:1:4%|古老巨龙骨:1:4%', '·限制时间结束|·3次力尽倒下', '达成任务', 50, 36000, '完成"银色的矜持"月辰的大团长', 'Reveal Thyself, Destroyer', '出でよ破壊の権化');
insert into t_quest values (null, 'M★6', 214, '自由', '银色的矜持', 'MR24', '大团长:有听到断角麒麟的传闻吗?据说它最近曾经在永霜冻土出没。令人好奇它为何突然出现，还有为什么折断了角!', '永霜冻土', '麒麟', '霸王之证:1:1% (保底)|太古龙骨:1:24%|古龙的净血:1:18%|灵晶原石:1:8%|重铠玉:1:24%|太古的宝珠:1:20%|古老巨龙骨:1:5%', '·限制时间结束|·3次力尽倒下', '讨伐麒麟', 50, 36000, '解锁聚魔之地MR24以上和大团长对话', 'Sterling Pride', '銀色の矜持');
insert into t_quest values (null, 'M★6', 218, '自由', '失去的幽世线', 'MR24', '龙人族的猎人:在聚魔之地发现了新的道路。气氛诡异，连小型怪物的踪迹也没有。以长远角度来看，地形出现变化并没有什么不寻常，但是…能量的动向似乎起了变化？来调查看看吧。', '聚魔之地', '', '太古的宝珠:1:1% (保底)|刚龙骨:1:38%|重龙骨:2:15%|坚铠玉:2:25%|龙晶原石:1:13%|纯水晶:1:4%|古老巨龙骨:1:4%', '·限制时间结束|·3次力尽倒下', '达成任务', 50, 36000, '完成冰原主线、狩猎雷狼龙后和月辰龙人族猎人对话', 'Across the Lost Path', '失われた幽世線');
