insert into t_quest values (null, 'M★6', 214, '自由', '爆华绽放', 'MR24', '威风的第五期团:师姐有一次被派往讨伐猛爆碎龙。她回来的时候，说了一句「花儿到处盛放」。这句话的意思，就由我们来确认吧。', '聚魔之地', '猛爆碎龙', '刻印的宝珠:1:8% (保底)|重铠玉:1:31%|刚龙骨:4:21%|灵晶原石:1:18%|纯水晶:1:8%|古老巨龙骨:1:8%|封印的宝珠:1:6%', '·限制时间结束|·3次力尽倒下', '无', 50, 43200, '完成"极限爆碎"', 'Achy Brachy Heart', '乱れ咲く連爆の華');
