insert into t_quest values (null, 'M★6', 213, '活动', '金狮金猴', 'MR24', '喂，是我啦！:目击到了一只极巨大的温泉金猴。是真的！那身参天耸立的金黄色体毛，就是温泉金猴的象征。看来我的灵感又要奔腾泉涌了。※报酬中会出现特别的素材', '永霜冻土', '激昂金狮子', '温泉金票:1:11% (保底)|金狮子的怒发天:1:19%|金狮子心:1:5%|罗剎的金刚角:1:12%|重铠玉:1:31%|封印的宝珠:1:16%|刻印的宝珠:1:6%', '·限制时间结束|·3次力尽倒下', '狩猎激昂金狮子', 50, 43200, '', 'Monkey Business', 'さるは金色、かわいいな');
insert into t_quest values (null, 'M★6', 214, '活动', '身裸万象', 'MR24', '就是这样，我又来了！:我突然领悟到，所有生物在原生的状态下，应该是最能发挥真正实力的…我会给你很多素材，再请你协助激发灵感了。※报酬中会出现特别的素材', '起源的孤岛', '天地煌啼龙', '三眼票:1:11% (保底)|天地煌啼龙的慈鳞:3:9%|天地煌啼龙的严岩壳:3:9%|天地煌啼龙的慈眼壳:1:18%|天地煌啼龙的散华石:1:11%|天地煌啼龙的慈爪:1:13%|天地煌啼龙的显玉:1:8%|王铠玉:1:15%|封印的宝珠:1:6%', '·限制时间结束|·3次力尽倒下', '讨伐天地煌啼龙', 50, 43200, '', 'The Naked Truth', '神裸袢消');
insert into t_quest values (null, 'M★6', 214, '活动', '彼岸泪', 'MR100', '内向的学者:在某些地方当故人去那边的世界时，虽然内心悲伤不舍，但为了不让其他人也被一同带走，会用可怕的面具作为装饰。调查尸套龙时不知为何想起了这个习俗。※报酬中会出现特别的素材', '瘴气之谷', '雾瘴尸套龙', '黑色的头巾:1:1% (保底)|灵脉玉碎片:1:24%|灵脉玉:1:21%|大灵脉玉:1:8%|死尸的厚龙鳞:2:13%|尸套龙的重壳:2:12%|王铠玉:1:21%', '·限制时间结束|·3次力尽倒下', '讨伐雾瘴尸套龙', 50, 43200, '', 'Tears from Nirvana', '彼岸涙');
insert into t_quest values (null, 'M★6', 213, '活动', '再会了，雷狼龙', 'MR24', '研究班队长:调查次数最多，真棒！既然如此要不要试试这个？是跟普通的比有点不一样的雷狼龙。就是得小心别捡东西捡得忘了形，被狠狠打到就不好了。', '斗技场', '雷狼龙', '特大龙脉炭:1:1% (保底)|雷狼龙票:1:24%|大龙脉炭:1:30%|龙脉炭:1:45%', '·限制时间结束|·3次力尽倒下', '狩猎雷狼龙', 50, 28800, '', 'A Farewell to Zinogre', 'ジンオウガよさらば');
insert into t_quest values (null, 'M★6', 213, '活动', '黑轰龙不会受伤', 'MR24', '阳光的推荐组员:调查次数最少，太过分了！看啊，虽然没受伤，可正在哭啊，不对是悲鸣！这家伙会掉落很多铠玉，跟别的有点不一样啊。※本任务无法取得怪物的素材', '斗技场', '黑轰龙', '真铠玉:1:1% (保底)|黑轰龙票:1:24%|王铠玉:1:30%|重铠玉:1:45%', '·限制时间结束|·3次力尽倒下', '狩猎黑轰龙', 50, 36000, '', 'Brand New Brute', '黒轟竜は傷つかない');
insert into t_quest values (null, 'M★6', 213, '活动', '你是黄金奖杯', 'MR100', '月辰的料理长:婆婆知道你其实真的很努力。你才是真正的黄金奖杯呢。所以婆婆为了这金色的任务准备了很棒的报酬啊。', '斗技场', '激昂金狮子', '大灵脉玉:1:1% (保底)|天之炼金票:1:63%|封印的宝珠:1:36%', '·限制时间结束|·5次力尽倒下', '狩猎激昂金狮子', 50, 210000, '', 'Mew are Number One!', '君はゴールデントロフィー');
insert into t_quest values (null, 'M★6', 214, '活动', '暗夜恒星', 'MR24', '研究班队长:幽境之谷观测到了煌黑龙的踪迹。宛如歌颂黑暗、光辉闪耀的璀璨之星。令人避讳其名的暗黑之光，就由调查团亲手揭开谜底！', '幽境之谷', '煌黑龙', '封印的宝珠:1:8% (保底)|王铠玉:1:24%|刻印的宝珠:1:13%|太古龙骨:2:21%|古龙的净血:2:18%|冥晶原石:1:16%', '·限制时间结束|·3次力尽倒下', '讨伐煌黑龙', 50, 43200, '', 'The Evening Star', '宵の恒星');
insert into t_quest values (null, 'M★6', 214, '活动', '黎明死星', 'MR24', '研究班队长:幽境之谷观测到了煌黑龙的踪迹。宛如扑灭光明、击退晨曦的霜寒之星。令人避讳其名的暗黑之光，就由调查团亲手揭开谜底！', '幽境之谷', '煌黑龙', '封印的宝珠:1:8% (保底)|王铠玉:1:24%|刻印的宝珠:1:13%|太古龙骨:2:21%|古龙的净血:2:18%|冥晶原石:1:16%', '·限制时间结束|·3次力尽倒下', '讨伐煌黑龙', 50, 43200, '', 'Dawn of the Death Star', '明けの死星');
insert into t_quest values (null, 'M★6', 214, '活动', '风暴的棺材', 'MR100', '回忆过去的第二旗团:年轻时，一尊钢龙经过了村庄的附近。面对前所未有的危机，那时我做好了把房子当成棺材的准备。虽然这些都过去了…我准备了封印的宝珠当作报酬。别让兵器置放处变成风暴的棺材。', '', '钢龙', '封印的宝珠:3:1% (保底)|封印的宝珠:1:8%|太古的宝珠:1:50%|刻印的宝珠:1:41%', '·限制时间结束|·3次力尽倒下', '无', 50, 43200, '', 'In the Tempest·s Wake', '嵐の棺');
insert into t_quest values (null, 'M★6', 214, '活动', '灭日', 'MR100', '剑术大师:太阳睡觉的地方竟然会烈焰冲天。真的是在燃烧的生命啊。结果无论是什么地方，生命只能选择生存之路。就如40年前的日子。我会准备封印的宝珠作完成时的报酬。', '', '炎王龙', '封印的宝珠:3:1% (保底)|封印的宝珠:1:8%|太古的宝珠:1:50%|刻印的宝珠:1:41%', '·限制时间结束|·3次力尽倒下', '无', 50, 43200, '', 'Day of Ruin', '滅日');
insert into t_quest values (null, 'M★6', 214, '活动', '我的高贵灵魂', 'MR100', '总司令:我们把燃烧的蓝火赶进了兵器置放处。就算是冰冷封闭的地方，那蓝火依然猛烈地燃烧着。调查班作好准备后前往兵器置放处。我会准备封印的宝珠当作报酬。', '', '炎妃龙', '封印的宝珠:3:1% (保底)|封印的宝珠:1:8%|太古的宝珠:1:50%|刻印的宝珠:1:41%', '·限制时间结束|·3次力尽倒下', '无', 50, 43200, '', 'The Cold Never Bothered Me', 'されど気高き我が魂よ');
insert into t_quest values (null, 'M★6', 214, '活动', '溟呼远响', 'MR100', '海色眼睛的猎人:我的母亲是猎师团的团长。以前因为大海传来轰声雷鸣，发觉太危险，唯有说服团员放弃远征。最近觉得那应该是溟波龙...我希望这次的调查会帮助所有的猎师。', '陆珊瑚台地', '溟波龙', '溟波龙票:1:11% (保底)|王铠玉:1:24%|刻印的宝珠:1:21%|封印的宝珠:1:18%|冥晶原石:1:18%|古龙的大宝玉:1:8%', '·限制时间结束|·3次力尽倒下', '讨伐溟波龙', 50, 54000, '', 'The Distant Dark Tide', '溟鳴り遥か遠く');
insert into t_quest values (null, 'M★6', 213, '活动', '最后的白骑士', 'MR24', '沉稳可靠的第五期团员:不畏严寒潜伏于树上多时，终于发现目标！那发散着酷寒之气的雄姿，彷佛「最后的白骑士」翩然而至。内心汹涌但不能轻敌，放下武器，先提供这次的目击情报吧。', '永霜冻土', '霜刃冰牙龙', '冰牙龙票:1:19% (保底)|重铠玉:1:21%|灵晶原石:1:15%|纯水晶:1:12%|瞬间冻结袋:1:16%|雪光金属:2:11%|封印的宝珠:1:6%', '·限制时间结束|·3次力尽倒下', '狩猎霜刃冰牙龙', 50, 43200, '', 'The Last White Knight', '終の白騎士');
insert into t_quest values (null, 'M★6', 214, '活动', '雪花沉睡', 'MR100', '第五期团的年轻猎人:我这次被任命为调查班长。除了指派的调查员，也有权募集可靠的同伴。调查对象是能力非常强大的冰呪龙。猎人们啊，如果你跃跃欲试，就与我们一起前往冰呪龙的沉睡之地吧！', '龙结晶之地', '冰呪龙', '冰呪龙票:1:11% (保底)|王铠玉:1:18%|刻印的宝珠:1:23%|封印的宝珠:1:24%|调查团票·大:1:24%', '·限制时间结束|·3次力尽倒下', '讨伐冰呪龙', 30, 57600, '', 'The Place Where Winter Sleeps', '六花が静かに眠るなら');
insert into t_quest values (null, 'M★6', 214, '活动', '传说中的黑龙', 'MR24', '总司令:国家正式委托调查团参加黑龙讨伐战。如果决心要面对那个只存在于传说中的恶梦，准备妥当后就接受任务吧。', '', '黑龙', '封印的宝珠:2:1% (保底)|封印的宝珠:1:11%|王铠玉:1:30%|黑龙的重壳:1:21%|黑龙的厚鳞:1:19%|黑龙的邪眼:1:5%|古龙的大宝玉:1:13%', '·限制时间结束|·3次力尽倒下', '讨伐黑龙', 30, 57600, '', 'Fade to Black', '伝説の黒龍');
insert into t_quest values (null, 'M★2', 213, '活动', '巨大气球x巨大气球', 'MR6', '嗨！是我！:留言板可以放在这里吧？因为找不到我中意的猎人，所以把消息贴在这里！如果愿意接受这个任务的话，头领说会额外提供铠玉！', '特殊斗技场', '浮空龙|浮眠龙', '坚铠玉:1:82% (保底)|重铠玉:1:18%', '·限制时间结束|·3次力尽倒下', '狩猎所有目标', 50, 28800, '', 'Balloon Fight', '巨大風船×巨大風船');
