insert into t_quest values (null, 'M★6', 214, '活动', '无终止的淘金记', 'MR24', '调查班队长:绚辉龙现身了。:这次的目标是成功讨伐:。去找编辑者或从任务板承接任务吧。请大家一定要小心它的暴怒状态，平安地回来！！', '地脉黄金乡', '绚辉龙', '刻印的宝珠:1:8% (保底)|封印的宝珠:1:6%|重铠玉:1:31%|灵晶原石:1:16%|太古龙骨:2:21%|古龙的净血:2:18%', '·限制时间结束|·3次力尽倒下', '讨伐绚辉龙', 50, 43200, '', 'The Eternal Gold Rush', '終わりなき黄金時代');
